import React from "react";
import { View, Text, ImageBackground, StyleSheet } from "react-native";
import { SIGNUP_BACKGROUND } from "../../constants";
import { Container, Content, Item, Input, Form, Button, H1 } from "native-base";
import { COLORS } from "../../constants";

const Signup = () => {
  return (
    <View style={styles.container}>
      <ImageBackground source={SIGNUP_BACKGROUND} style={styles.image}>
        <Text style={styles.title}>Signup</Text>
        <Form style={styles.form}>
          <Item style={styles.input}>
            <Input placeholder="Email" />
          </Item>
          <Item style={styles.input}>
            <Input placeholder="Password" />
          </Item>
          <Item style={styles.input}>
            <Input placeholder="Confirm password" />
          </Item>
          <Text style={[styles.link, styles.margin]}>Terms and service</Text>
          <Text style={styles.text}>
            Already have an account? <Text style={styles.link}>Signin</Text>
          </Text>

          <Button style={styles.button} rounded success>
            <Text>Go</Text>
          </Button>
        </Form>
      </ImageBackground>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "column"
  },
  image: {
    width: "100%",
    height: "100%",

    resizeMode: "cover"
  },
  title: {
    marginTop: "10vh",
    color: COLORS.WHITE,
    fontSize: 40,
    fontWeight: "bold",
    textAlign: "center",
    marginBottom: "20px"
  },
  link: {
    color: "blue"
  },

  input: {
    marginTop: 10,
    backgroundColor: COLORS.WHITE,
    minWidth: 200,
    maxWidth: 300,
    borderRadius: 50
  },
  form: {
    alignItems: "center"
  },
  terms: {
    marginTop: 10,
    marginBottom: 10,
    alignItems: "center"
  },
  termText: {
    color: "black",
    width: 200,
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 12
  },
  button: {
    display: "flex",
    justifyContent: "center",
    marginTop: 10,
    width: 100
  }
});

export default Signup;
